print(default_api.write_file(path="./test_main.py", content="import unittest\nimport main\n\nclass TestDistance(unittest.TestCase):\n    def test_positive_inputs(self):\n        self.assertAlmostEqual(main.calculate_distance(10, 2, 5), 10+ 0.5*2*5**2)\n    \n    def test_zero_time(self):\n        self.assertAlmostEqual(main.calculate_distance(10, 2, 0), 10)\n    \n    def test_negative_time(self):\n        self.assertRaises(ValueError, main.calculate_distance,10,2,-1)\n    \n    def test_zero_velocity(self):\n        self.assertAlmostEqual(main.calculate_distance(0,2,5), 0.5 * 2 * 5**2)\n    \n    def test_negative_velocity(self):\n        self.assertAlmostEqual(main.calculate_distance(-10, 2,5), -10 + 0.5*2*5**2)\n    \n    def test_zero_acceleration(self):\n        self.assertAlmostEqual(main.calculate_distance(10,0,5), 10*5)\n    \n    def test_negative_acceleration(self):\n        self.assertAlmostEqual(main.calculate_distance(10,-2,5), 10*5 +0.5*-2*5**2)\n\nif __name__ == '__main__':\n    unittest.main()"))